INSERT INTO `spiders_news_content` VALUES ('2440', '-5716123991568058823', '<table align=\"center\" border=\"0\" cellpadding=\"1\" cellspacing=\"1\" id=\"tblInfo\" runat=\"server\" width=\"100%\">\n<tr>\n<td align=\"center\" id=\"tdTitle\" runat=\"server\"><div style=\"text-align:left\">\n</div>\n<div><font class=\"infotitle\" color=\"000000\">\r\n                        2007—21号地块规划条件调整公示说明\r\n                       </font></div>\n<br/>\n<div style=\"font-size:18px; font-family:楷体;\"> </div>\n<hr size=\"1\"/>\n<font class=\"webfont\" color=\"#888888\">【信息时间：\r\n2013/3/13\r\n                          阅读次数：<span id=\"infoclicktimes\" infoidsiteguid=\"1774302f-348e-47a9-a682-44158bbad9b5|cf0a9053-4b12-42ae-a0d2-df8363ff2dfd\"></span>\r\n                        】<a href=\"javascript:void(0)\" onclick=\"window.print();\"><font class=\"webfont\" color=\"#000000\">【我要打印】</font></a><a href=\"javascript:window.close()\"><font class=\"webfont\" color=\"#000000\">【关闭】</font></a></font><font color=\"#000000\">\n</font></td>\n</tr>\n<tr>\n<td height=\"10\"></td>\n</tr>\n<tr>\n<td class=\"infodetail\" height=\"250\" id=\"TDContent\" valign=\"top\"><center><table border=\"0\" width=\"600\"><tr><td><span style=\"font-size:12pt;color=’#000000’\"><pre>                     2007—21号地块规划条件调整公示说明<br/>               <br/>2007-21号地块（龙商&amp;#8226;荣域世家）项目用地位于莲花井巷南，奎河西原幼师地块，<br/>规划用地A地块13677.1平方米、B地块4943.4平方米，规划要点A地块为商住用地，<br/>容积率＜3.5、建筑密度≤26%、绿地率≥25%；B地块为商办用地，容积率≤1.8、<br/>建筑密度≤40%、绿地率≥20%，且A地块需配备幼托一所。<br/>为营造沃尔玛地区商业中心的需要，江苏龙商实业投资有限公司申请调整A地块建<br/>筑密度（由26%调增至40%），同时拟将原A地块内的幼儿园位置调至B地块内安排，<br/>原规划容积率及其他指标不变。现将2007-21号地块（龙商&amp;#8226;荣域世家）地块规划设<br/>计条件调整方案向广大市民公示，请关心该地块建设的市民提出合理建议，以便完<br/>善规划设计。<br/>                                          联系人：  李雪英<br/>                                          联系电话：85586028<br/>                                                           徐州市规划局<br/>                                                           2010年12月3日<br/></pre></span></td></tr></table></center><br/><br/><center><img src=\"/ghj/old/lanmu/images/总图11.22-2_t3_t3-Model.jpg\"/></center></td>\n</tr>\n<tr>\n<td align=\"right\" class=\"infodetailauthor\">\n<br/>\n</td>\n</tr>\n<tr id=\"trAttach\" runat=\"server\">\n<td><table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" id=\"filedown\" runat=\"server\" width=\"100%\">\n<tr>\n<td valign=\"top\"></td>\n</tr>\n</table></td>\n</tr>\n<tr>\n<td></td>\n</tr>\n<tr>\n<td height=\"10\"></td>\n</tr>\n<tr>\n<td></td>\n</tr>\n</table>', '1', '2018-06-25 11:38:09', 'http://gh.xz.gov.cn/ghj/ghgs/20130313/026_1774302f-348e-47a9-a682-44158bbad9b5.htm', '1', 'test25000001', 'test25000002');
