INSERT INTO `spiders_news_content` VALUES ('2446', '5658818840881166594', '<table align=\"center\" border=\"0\" cellpadding=\"1\" cellspacing=\"1\" id=\"tblInfo\" runat=\"server\" width=\"100%\">\n<tr>\n<td align=\"center\" id=\"tdTitle\" runat=\"server\"><div style=\"text-align:left\">\n</div>\n<div><font class=\"infotitle\" color=\"000000\">\r\n                        新城区二号地块C地块变更公示说明\r\n                       </font></div>\n<br/>\n<div style=\"font-size:18px; font-family:楷体;\"> </div>\n<hr size=\"1\"/>\n<font class=\"webfont\" color=\"#888888\">【信息时间：\r\n2013/3/13\r\n                          阅读次数：<span id=\"infoclicktimes\" infoidsiteguid=\"e42423a7-1fe5-47c8-bdfd-482ebb179d76|cf0a9053-4b12-42ae-a0d2-df8363ff2dfd\"></span>\r\n                        】<a href=\"javascript:void(0)\" onclick=\"window.print();\"><font class=\"webfont\" color=\"#000000\">【我要打印】</font></a><a href=\"javascript:window.close()\"><font class=\"webfont\" color=\"#000000\">【关闭】</font></a></font><font color=\"#000000\">\n</font></td>\n</tr>\n<tr>\n<td height=\"10\"></td>\n</tr>\n<tr>\n<td class=\"infodetail\" height=\"250\" id=\"TDContent\" valign=\"top\"><center><table border=\"0\" width=\"600\"><tr><td><span style=\"font-size:12pt;color=’#000000’\"><pre>              新城区二号地块C地块变更公示说明<br/><br/>新城区二号地块C地块项目用地位于新城区，规划用地14.0807公顷，该项目规划设<br/>计于2008年7月28日经我局批准，现建设单位对新城区二号地块C地块未办理工程许<br/>可部分的规划设计方案提出了修改申请，我局对修改后规划设计进行审查，符合规<br/>划设计要求。现将新城区二号地块C地块规划设计及主要技术经济指标向广大市民<br/>公示，请关心该地块建设的市民提出合理建议，以便完善规划设计.<br/><br/>                                   联系人：  李雪英<br/>                                   联系电话：85586028<br/>                                                          徐州市规划局<br/>                                                          2010年12月3日<br/><br/>注：上为原总平面图<br/>    下为新总平面图<br/></pre></span></td></tr></table></center><br/><br/><center><img src=\"/ghj/old/lanmu/images/原总平面图-2008071-布局1.jpg\"/></center><br/><center><img src=\"/ghj/old/lanmu/images/新ZS-02-2010.11.23-二标段总图.jpg\"/></center></td>\n</tr>\n<tr>\n<td align=\"right\" class=\"infodetailauthor\">\n<br/>\n</td>\n</tr>\n<tr id=\"trAttach\" runat=\"server\">\n<td><table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" id=\"filedown\" runat=\"server\" width=\"100%\">\n<tr>\n<td valign=\"top\"></td>\n</tr>\n</table></td>\n</tr>\n<tr>\n<td></td>\n</tr>\n<tr>\n<td height=\"10\"></td>\n</tr>\n<tr>\n<td></td>\n</tr>\n</table>', '1', '2018-06-25 11:38:09', 'http://gh.xz.gov.cn/ghj/ghgs/20130313/026_e42423a7-1fe5-47c8-bdfd-482ebb179d76.htm', '1', 'test25000001', 'test25000002');
